mapper.select:
SELECT * FROM MEMBER
WHERE NAME = #{name}
AND PHONNUM = #{phonnum}

SELECT * FROM MEMBER
WHERE ID = #{id}
AND EMAIL = #{email}

		SELECT ID, PASSWORD, NAME, YEAR, MONTH, DAY
		, PHONNUM, LOCATION, EMAIL, PROFILE_IMG, C.COMMON_NAME
		FROM MEMBER M INNER JOIN COMMON C
            ON M.LOCATION = c.common_code
		WHERE ID = #{id}
		AND PASSWORD = #{password}
